@pytest.yield_fixture()
def rpc_server():
    from testrpc.server import get_application

    application = get_application()

    port = get_open_port()

    server = make_server(
        '127.0.0.1',
        port,
        application,
    )
    thread = spawn(server.serve_forever)
    wait_for_http_connection(port)

    yield server

    try:
        server.stop()
    except AttributeError:
        server.shutdown()
    thread.join()


nonce = 0


@pytest.fixture()
def rpc_client(rpc_server):
    from testrpc.client.utils import force_obj_to_text

    try:
        host, port = rpc_server.address
    except AttributeError:
        host, port = rpc_server.server_address

    endpoint = "http://{host}:{port}".format(host=host, port=port)

    def make_request(method, params=None):
        global nonce
        nonce += 1  # NOQA
        payload = {
            "id": nonce,
            "jsonrpc": "2.0",
            "method": method,
            "params": params or [],
        }
        payload_data = json.dumps(force_obj_to_text(payload, True))

        if 'TESTRPC_ASYNC_GEVENT' in os.environ:
            from geventhttpclient import HTTPClient
            client = HTTPClient(
                host=host,
                port=port,
                connection_timeout=10,
                network_timeout=10,
                headers={
                    'Content-Type': 'application/json',
                },
            )
            with contextlib.closing(client):
                response = client.post('/', body=payload_data)
                response_body = response.read()

            result = json.loads(force_text(response_body))
        else:
            import requests
            response = requests.post(
                endpoint,
                data=payload_data,
                headers={
                    'Content-Type': 'application/json',
                },
            )

            result = response.json()

        if 'error' in result:
            raise AssertionError(result['error'])

        return result['result']

    make_request.server = rpc_server

    return make_request


