@pytest.fixture()
def client_call_emitter(client, accounts, emitter_contract_address):
    def _client_call_emitter(method_signature, arguments=None):
        if arguments is None:
            arguments = []
        function_sig = encode_data(sha3(method_signature)[:4])
        data = function_sig + b''.join((strip_0x(encode_number(arg, 32)) for arg in arguments))
        assert len(data) == 2 + 8 + 64 * len(arguments)

        txn_hash = client.send_transaction(
            _from=accounts[0],
            to=emitter_contract_address,
            data=data,
            gas=200000,
        )
        return txn_hash
    return _client_call_emitter


